  function addMarker(type, location, lat, lng, link, imgPath, imgWidth, imgHeight, title, coords, schedule)
  {
    var icon = 'http://maps.google.com/mapfiles/marker.png';
    switch(type)
    {
      case 1:
        if (title == null) title = "Radio Station";
        icon = '<%= images_url %>/radio-station.png';
        break;  
      case 2:
        if (title == null) title = "Radio Translator";
        icon = '<%= images_url %>/radio-translator.png';
        break;
      case 3:
        if (title == null) title = "Tract Trip";
        icon = 'http://maps.google.com/mapfiles/marker.png';
        break;        
      case 4:
        if (title == null) title = "Billboard";
        icon = 'http://maps.google.com/mapfiles/marker_green.png';
        break;      
      case 5:
        if (title == null) title = "Print Ad";
        icon = 'http://maps.google.com/mapfiles/marker_yellow.png';
        break;
      case 6:
        if (title == null) title = "New Item";
        icon = 'http://maps.google.com/mapfiles/marker_purple.png';
        break;
    }
    var scheduleContent = "";
    if (schedule != null) {
      scheduleContent = '<table class="schedule"><tr><th>Frequency<\/th><th>Hours<\/th><th>Days<\/th><\/tr>';
      for (var i=0;i<schedule.frequency.length;i++) {
        scheduleContent += '<tr><td>'+schedule.frequency[i]+'<\/td><td>'+schedule.hours[i]+'<\/td><td>'+schedule.days[i]+'<\/td><\/tr>';
      }
      scheduleContent += '<\/table>';
    }
    var locationContent = '<p style="text-align: center; margin: 0px 0px 0.5em;">Location: ';
    if (link != null) locationContent += '<a href="'+link+'"><b>'+location+'<\/b><\/a><\/p>';
    else locationContent += '<b>'+location+'<\/b><\/p>';    
    var imgContent = "";
    if (imgPath != null) {
      imgContent = '<img src="'+imgPath+'" width="'+imgWidth+'" height="'+imgHeight+'" alt="Photo">';
      if (link != null) imgContent = '<a href="'+link+'">'+imgContent+'<\/a>';
    }    
    var contentString = '<div style="text-align: center; overflow: hidden;"><p style="text-align: center; margin: 0px 0px 0.5em;"><b>'+title+'<\/b><\/p>'+locationContent+imgContent+scheduleContent+'<\/div>';
    var marker = new google.maps.Marker({
        position: new google.maps.LatLng(lat, lng),
        map: map,
        title: location,
        icon: icon
    });
    google.maps.event.addListener(marker, 'click', function() {
      infowindow.setContent(contentString);
      infowindow.open(map,marker);
    });
    
    if (type <= 2 && coords != null) {
      var contentStringPolygon = '<div style="text-align: center; overflow: hidden;"><p style="text-align: center; margin: 0px 0px 0.5em;"><b>Coverage Area<br>'+title+'<\/b><\/p><\/div>';
      var polygon = new google.maps.Polygon({
        paths: coords,
        map: map,
        fillColor: '#00AAFF',
        fillOpacity: 0.4,
        strokeOpacity: 0.0
      });
      google.maps.event.addListener(polygon, 'click', function() {
        infowindow.setContent(contentStringPolygon);
        infowindow.open(map,marker);
      });
    }
  }  
